mapper.select:
select * from t_fpj where yxbz = '1'

    and djh = #{djh}


    and unionid = #{unionid}

limit 1

select a.djh, a.gfmc, a.jylssj, a.jshj, c.ztbz, c.ckbtgyy from t_jyls a right join t_fpj b on a.djh=b.djh left join t_ckhk c ON a.djh=c.djh where a.yxbz='1' and b.yxbz='1' and a.clztdm='91'
and b.unionid = #{unionid}
order by b.id desc
